import React from 'react'
import styled from 'styled-components';

const Main = styled.main`
    background-color:#eceae9;

    display:flex;
    justify-content:center;
    align-items:center;
`;

const FileInput = styled.label`
    color:#4c4a49;
    font-family: 'GmarketSansMedium', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size: 3.5rem;
    line-height:2;
    text-shadow:1px 1px 1px #5c5a59;

    &:hover {
        transform: scale(1.1);
        transition: all 0.1s ease;
    };
`;

const Home = () => {
    const parseFile = (e) => {
        const reader = new FileReader();

        reader.onload = () => {
            const fileData =  reader.result;
            const wb = XLSX.read(fileData, {type : 'binary'});
            const firstSheetName = wb.SheetNames[0];
            const sheet = wb.Sheets[firstSheetName];

            mainSheet = []; 
            let range = XLSX.utils.decode_range(sheet['!ref']);

            for(let R=2; R<=range.e.r; ++R) {
                let rowData = [];

                rowData.push(getElem(sheet, 0, R));
                rowData.push(getElem(sheet, 1, R));
                rowData.push(getElem(sheet, 2, R));

                mainSheet.push(rowData);
            }
        }

        reader.readAsBinaryString(e.target.files[0]);
        fileName[0].innerHTML = e.target.files[0].name;
        console.log(mainSheet);
    };

    return (
        <Main>
            <FileInput>
                파일 올리기
                <input type="file" onChange={parseFile} />
            </FileInput>
        </Main>
    );
};

export default Home;
