import React, { useState } from 'react'
import styled from 'styled-components';
import useWindowSize from '../hooks/useWindowSize';

const Main = styled.main`
    background-color:#eceae9;

    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;
`;

const FileInput = styled.label`
    color:#4c4a49;
    font-family: 'GmarketSansMedium', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size: 3.5rem;
    line-height:2;
    text-shadow:1px 1px 1px #5c5a59;

    &:hover {
        transform: scale(1.1);
        transition: all 0.1s ease;
    };
`;

const Button = styled.button`
    color:#4c4a49;
    font-family: 'GmarketSansMedium', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size: 3.5rem;
    line-height:2;
    text-shadow:1px 1px 1px #5c5a59;

    &:hover {
        transform: scale(1.1);
        transition: all 0.1s ease;
    };
`;

function Worker(name) {
    this.name = name;
    this.workTime = []; // [ [ date, time], ... ]

    this.getSum = () => {
        return workTime.reduce((result, data) => {
            return result + data[1];
        });
    }
}

const getElem = (sheet, i, R) => {
    let tmp = sheet[XLSX.utils.encode_cell({c:i, r:R})]
    let elem = 'UNKNOWN';
    if(tmp && tmp.t) elem = XLSX.utils.format_cell(tmp);

    return elem;
}

const Home = () => {
    const winSize = useWindowSize();
    const [mainFile, setMainFile] = useState(null);

    const parseFile = (e) => {
        const reader = new FileReader();
        console.log('test');

        reader.onload = () => {
            const fileData =  reader.result;
            const wb = XLSX.read(fileData, {type : 'binary'});
            const firstSheetName = wb.SheetNames[0];
            const sheet = wb.Sheets[firstSheetName];

            const mainSheet = []; 
            let range = XLSX.utils.decode_range(sheet['!ref']);

            for(let R=2; R<=range.e.r; ++R) {
                let rowData = [];

                rowData.push(getElem(sheet, 0, R));
                rowData.push(getElem(sheet, 1, R));
                rowData.push(getElem(sheet, 2, R));

                mainSheet.push(rowData);
            }

            setMainFile(mainSheet);
        }

        reader.readAsBinaryString(e.target.files[0]);
    };

    const getResult = () => {
        mainFile.sort((a, b) => {
            return a[0] - b[0];
        });

        const workers = []; // array of Worker
        let idx = 0;
        while(idx < mainFile.length) {
            const name = mainFile[idx];
            const rows = []
            const _worker = new Worker(name);

            while(mainFile[idx] === name) rows.push(mainFile[idx++]);

            let idx2 = 0;
            while(idx2 < rows.length) {
