            }

        }
    };

    return (
        <Main style={{height:winSize[1]}}>
            <FileInput>
                파일 올리기
                <input type="file" style={{display:'none'}} onChange={parseFile} />
            </FileInput>
            <Button onClick={getResult}>
                결과 보기
            </Button>
        </Main>
    );
};

export default Home;
